mapper.select:
SELECT count(DISTINCT uid)
FROM borrowapply
WHERE state = 1;


SELECT count(DISTINCT uid)
FROM borrowapply
WHERE state = 1 AND time > #{beginTime} AND time < #{endTime};

SELECT count(baid)
FROM borrowapply
WHERE state = 1;


SELECT count(baid)
FROM borrowapply
WHERE state = 1 AND time > #{beginTime} AND time < #{endTime};


SELECT *
FROM borrowapply
WHERE state = 1 AND deadline < #{nowTime};

mapper.sql:
FROM borrowapply b, bz, jklx j, user u WHERE b.uid = u.uid AND b.bzid = bz.bzid AND b.type = j.lxid AND bz.state = 1 AND j.state = 1
AND b.uid = #{query.uid}
AND b.state = #{query.state}
AND b.state not in (0, 2, 3)

mapper.select:
SELECT b.baid,b.rname,b.money, b.state, b.deadline, b.term, b.time AS 'applyTime',
b.symoney, bz.bzname, j.lxname

LIMIT #{pager.offset}, #{pager.limit}

select ba.baid, bd.cpname, ba.money, bd.nprofit, ba.term, bz.bzname, jklx.lxname, ba.state
from borrowapply ba, borrowdetail bd, bz bz, jklx jklx
where ba.baid = bd.baid and ba.bzid = bz.bzid and ba.type = jklx.lxid and (ba.state = 1 or ba.state = 4)

  and #{xmqx1} <= ba.term


  and ba.term <= #{xmqx2}


  and #{nysy1} <= bd.nprofit


  and bd.nprofit <= #{nysy2}


  and bz.bzname = #{xmlx1}


    and bd.cpname like concat('%',#{search},'%')

mapper.sql:
FROM borrowapply b, bz, jklx j WHERE b.bzid = bz.bzid AND b.type = j.lxid AND bz.state = 1 AND j.state = 1
AND b.state = #{query.state}
AND b.state not in (0, 2, 3)

mapper.select:
SELECT b.baid,b.rname,b.money, b.state, b.deadline, b.term, b.time AS 'applyTime',
b.symoney, bz.bzname, j.lxname

LIMIT #{pager.offset}, #{pager.limit}
