mapper.update:
update notice
set title = #{title,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP},
  content = #{content,jdbcType=LONGVARCHAR}
where nid = #{nid,jdbcType=INTEGER}

update notice
set title = #{title,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP}
where nid = #{nid,jdbcType=INTEGER}

mapper.select:
SELECT ul.luid AS 'id', ul.state, n.content, n.title, n.date FROM notice n,user_letter ul, user u WHERE u.uid = ul.uid AND ul.lid = n.nid -- AND n.state = 1 AND ul.state != 0
AND ul.uid = #{query.uid}
ORDER BY ul.state DESC, n.date DESC LIMIT #{pager.offset},#{pager.limit}
